    private void paymentMethod() {
        try {
            ResultSet rs = MySQL.search("SELECT * FROM `payment_method` WHERE `id`<>3");

            Vector v = new Vector();
            v.add("Select");

            while (rs.next()) {
                v.add(rs.getString("name"));
            }

            jPayementMethod.setModel(new DefaultComboBoxModel(v));

        } catch (Exception e) {
            e.printStackTrace();
        }

    }

    private void calTotal() {
        double total = 0;

        int r = jTable1.getRowCount();
        for (int i = 0; i < r; i++) {

            total += Double.valueOf(jTable1.getValueAt(i, 6).toString());
        }

        jTotal.setText(df.format(total));
        String payement = jPayment.getText();
        double balance = 0;
        if (payement.isEmpty()) {
            if (total != 0) {
                balance = -total;

            }
        } else {
            balance = Double.valueOf(payement) - total;
        }
        if (balance < 0) {
            jBalance.setForeground(Color.RED);
        } else {
            jBalance.setForeground(Color.GREEN);

        }
        if (total == 0) {
            jBalance.setForeground(b);

        }
        jBalance.setText(df.format(balance));
    }

