    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        String stockId = jStockId.getText();
        String pname = jMedicine.getText();
        String category = jLabel28.getText();
        String aqty = jLabel29.getText();
        String mfd = jLabel30.getText();
        String exd = jLabel31.getText();
        String price = jLabel32.getText();
        String qty = jSpinner1.getValue().toString();
        if (stockId.equals("None")) {
            JOptionPane.showMessageDialog(this, "Please select medicine", "Warning", JOptionPane.WARNING_MESSAGE);

        } else {
            int r = jTable1.getRowCount();
            int row = -1;
            DefaultTableModel dtm = (DefaultTableModel) jTable1.getModel();
            boolean isFound = false;
            for (int i = 0; i < r; i++) {
                String pid = jTable1.getValueAt(i, 0).toString();
                if (pid.equals(stockId)) {
                    isFound = true;
                    row = i;
                }
            }
            Vector v = new Vector<>();

            if (isFound) {
                int opt = JOptionPane.showConfirmDialog(this, "Seems this product already exist in bill. Do you want to update quantity?", "Warning", JOptionPane.YES_NO_OPTION, JOptionPane.INFORMATION_MESSAGE);
                if (opt == JOptionPane.YES_OPTION) {
                    v.add(stockId);
                    v.add(pname);
                    v.add(category);
                    v.add(exd);
                    int oldqty = Integer.valueOf(jTable1.getValueAt(row, 4).toString());
                    int newqty = Integer.valueOf(qty) + oldqty;
                    v.add(newqty);
                    v.add(price);
                    v.add(df.format(Double.valueOf(price) * Integer.valueOf(newqty)));
                    dtm.removeRow(row);
                    dtm.addRow(v);
                }
            } else {
                v.add(stockId);
                v.add(pname);
                v.add(category);
                v.add(exd);
                v.add(qty);
                v.add(price);
                v.add(df.format(Double.valueOf(price) * Integer.valueOf(qty)));
                dtm.addRow(v);
            }

            jStockId.setText("None");
            jMedicine.setText("None");
            jLabel28.setText("None");
            jLabel29.setText("None");
            jLabel30.setText("None");
            jLabel31.setForeground(b);
            jLabel31.setText("None");
            jLabel32.setText("None");
            jSpinner1.setValue(1);
            calTotal();
        }
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        String method = jPayementMethod.getSelectedItem().toString();
        String payment = jPayment.getText();
        String balance = jBalance.getText();
        String total = jTotal.getText();
        int r = jTable1.getRowCount();

        if (r == 0) {
            JOptionPane.showMessageDialog(this, "Please add medicines", "Warning", JOptionPane.WARNING_MESSAGE);

        } else if (method.equals("Select")) {
            JOptionPane.showMessageDialog(this, "Please select payment method", "Warning", JOptionPane.WARNING_MESSAGE);

        } else if (payment.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Please enter payement", "Warning", JOptionPane.WARNING_MESSAGE);

        } else if (Double.valueOf(balance) < 0) {
            JOptionPane.showMessageDialog(this, "Payement is low than total price", "Warning", JOptionPane.WARNING_MESSAGE);

        } else {
            try {

                ResultSet rs = MySQL.search("SELECT * FROM `payment_method` WHERE `name`='" + method + "'");
                rs.next();
                String methodId = rs.getString("id");
                String uniqeId = System.currentTimeMillis() + "-" + LogIn.UserId;

                MySQL.iud("INSERT INTO `invoice` (`date_time`,`user_id`,`unique_id`) VALUES ('" + datef.format(new Date()) + "','" + LogIn.UserId + "','" + uniqeId + "')");
                ResultSet rs1 = MySQL.search("SELECT * FROM `invoice` WHERE `unique_id`='" + uniqeId + "'");
                rs1.next();
                String invoid = rs1.getString("id");
                MySQL.iud("INSERT INTO `invoice_payment` (`balance`,`payment`,`payment_method_id`,`invoice_id`) VALUES ('" + Double.valueOf(balance) + "','" + payment + "','" + methodId + "','" + invoid + "')");
                for (int i = 0; i < r; i++) {
                    String qty = jTable1.getValueAt(i, 4).toString();
                    String stockId = jTable1.getValueAt(i, 0).toString();

                    String exd = jTable1.getValueAt(i, 3).toString();
                    String sellingPrice = jTable1.getValueAt(i, 5).toString();

                    ResultSet rs2 = MySQL.search("SELECT * FROM `stock` WHERE `id`='" + stockId + "' ");
                    rs2.next();
                    int oldQty = Integer.valueOf(rs2.getString("qty"));
                    int newQty = oldQty - Integer.valueOf(qty);
                    MySQL.iud("UPDATE `stock` SET `qty`='" + newQty + "' WHERE `id`='" + stockId + "'");
                    MySQL.iud("INSERT INTO `invoice_item` (`stock_id`,`invoice_id`,`qty`) VALUES ('" + stockId + "','" + invoid + "','" + qty + "')");

                }
                //                InputStream pathStream = Billing.class.getResourceAsStream("../reports/Invoice.jasper");

                String pathStream = "src/reports/Invoice.jasper";
                //                JasperReport jr = JasperCompileManager.compileReport(path);
                HashMap parameters = new HashMap();
                parameters.put("code", uniqeId);
                parameters.put("barcode", uniqeId);
                parameters.put("todate", datef.format(new Date()));
                parameters.put("payment", df.format(Double.valueOf(payment)));
                parameters.put("balance", balance);
                parameters.put("paymethod", method);
                parameters.put("total", total);
                Vector<model.Invoice> v = new Vector();
                for (int i = 0; i < r; i++) {
                    String stockid = jTable1.getValueAt(i, 0).toString();
                    String name = jTable1.getValueAt(i, 1).toString();
                    String qty = jTable1.getValueAt(i, 4).toString();
                    String price = jTable1.getValueAt(i, 5).toString();
                    String total1 = jTable1.getValueAt(i, 6).toString();
                    v.add(new Invoice(stockid, name, qty, price, total1));
                }
                JRBeanCollectionDataSource dataSource = new JRBeanCollectionDataSource(v);
                JasperPrint jp = JasperFillManager.fillReport(pathStream, parameters, dataSource);
                JasperViewer.viewReport(jp, false);

                jPayementMethod.setSelectedIndex(0);
                jPayment.setText("");
                jBalance.setText("0.00");
                jTotal.setText("0.00");
                jBalance.setForeground(b);
                DefaultTableModel dtm = (DefaultTableModel) jTable1.getModel();
                dtm.setRowCount(0);
                JOptionPane.showMessageDialog(this, "New bill added success", "Success", JOptionPane.INFORMATION_MESSAGE,successIcon);

            } catch (Exception e) {
                e.printStackTrace();
            }
        }

    }//GEN-LAST:event_jButton4ActionPerformed

    private void jPaymentKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jPaymentKeyReleased
        String price = jPayment.getText();

        if (!Pattern.compile("0|0[.]|0[.][0-9]{0,1}|0[.][0-9]{0,1}[1-9]{0,1}|[1-9]+|[1-9]+[0-9]+|[1-9]+[.]|[1-9]+[0-9]*[.][0-9]{0,2}").matcher(price).matches()) {
            jPayment.setText("");

        }
        calTotal();
    }//GEN-LAST:event_jPaymentKeyReleased

    private void jPaymentKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jPaymentKeyTyped
        String price = jPayment.getText();

        if (!Pattern.compile("0|0[.]|0[.][0-9]{0,1}|0[.][0-9]{0,1}[1-9]{0,1}|[1-9]+|[1-9]+[0-9]+|[1-9]+[.]|[1-9]+[0-9]*[.][0-9]{0,2}").matcher(price + evt.getKeyChar()).matches()) {
            evt.consume();
        }
    }//GEN-LAST:event_jPaymentKeyTyped


